    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        // TODO add your handling code here:
    float r, a;
    TCilindro Cil = new TCilindro();
    String salida="DATOS DEL C I L I N D R O.\n";
    r= Float.parseFloat(txtRadio.getText());
    a= Float.parseFloat(txtAltura.getText());
    Cil.cargar_datos(r, a);
    txaSalida.setText(salida+"Volumen del Cilindro= " + Cil.ver_areaCil());
    }//GEN-LAST:event_btnCalcularActionPerformed

