    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        // TODO add your handling code here:
        TCoronaCircular Corona= new TCoronaCircular();
    double rad1, rad2;
    String salida="";
    rad1= Double.parseDouble(txtRadio1.getText());
    rad2= Double.parseDouble(txtRadio2.getText());
    Corona.inicializar(rad1, rad2);
    Corona.calcularSCC();
    salida+="Radio 1= "+Corona.C1.ver_radio()+
"\nRadio 2= "+Corona.C2.ver_radio()+
"\nPerimetro Interior= "+Corona.C1.ver_perimetro()+
"\nPerimetro Exterior= "+Corona.C2.ver_perimetro()+
"\nSuperficie Corona= " + Corona.ver_superficieCC();
txaSalida.setText(salida);
    }//GEN-LAST:event_btnCalcularActionPerformed

