    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        // TODO add your handling code here:
        TFigura F;
        F = new TFigura();
        double a,b,c,d;
        a= Double.parseDouble(txtBaseR1.getText());
        b= Double.parseDouble(txtAlturaR1.getText());
        c= Double.parseDouble(txtBaseR2.getText());
        d= Double.parseDouble(txtAlturaR2.getText());
        F.datosFiguraT(a, b, c, d);
        F.calcularAreaTotal();
        txtSuperficieT.setText(Double.toString(F.verAreaTotal()));
    }//GEN-LAST:event_btnCalcularActionPerformed

